import {Dispatch} from 'redux'
import {AuthAPI} from '../dal/api';


type setErrorACType = {
    type: 'APP/SET-ERROR'
    error?: string
}

type setStatusACType = {
    type: 'APP/SET-STATUS'
    error?: string
}

type initialStateType = {
    status: boolean
    error?: string | null
}

const initialState = {
    status: false,
    error: null
}

type ActionsType = setErrorACType | setStatusACType

export const registerReducer = (state: initialStateType = initialState, action: ActionsType):initialStateType => {
    let copyState = {...state}
    switch (action.type) {
        case 'APP/SET-ERROR':
            if (action.error) {
                return {
                    ...state,
                    error: action.error
                }
            } else {
                return copyState
            }
        case 'APP/SET-STATUS':

        default:
            return state
    }
}

export const setStatusAC = (status: boolean) => ({type: 'APP/SET-STATUS', status} as const)
export const setErrorAC = (error: string | null) => ({type: 'APP/SET-ERROR', error} as const)

export const CreateUserThunk = (email: string, password: string) => {
    return (dispatch: Dispatch) => {
        dispatch(setStatusAC(true))
        AuthAPI.createUser(email, password)
            .then((res) => {
                dispatch(setStatusAC(false))
            })
            .catch((res) => {
                const error = res.error
                dispatch(setErrorAC(error))
                dispatch(setStatusAC(false))
            })
    }
}
