import {Dispatch} from 'redux'
import {AuthAPI} from '../dal/api';

type createUserDataACType = {
    type: 'CREATE-USER-DATA'
    error?: string
}

type initialStateType = {
    error?: string | null
}

const initialState = {
    error: null
}

export const registerReducer = (state: initialStateType = initialState, action: createUserDataACType):initialStateType => {
    let copyState = {...state}
    switch (action.type) {
        case 'CREATE-USER-DATA':
            if (action.error) {
                return {
                    ...state,
                    error: action.error
                }
            } else {
                return copyState
            }
        default:
            return state
    }
}

const createUserData = (error: string): createUserDataACType => ({type: 'CREATE-USER-DATA', error})

export const CreateUserThunk = (email: string, password: string) => {
    return (dispatch: Dispatch) => {
        AuthAPI.createUser(email, password)
            .then((res) => {

            })
            .catch((res) => {
                const error = res.addedUser.error
                dispatch(createUserData(error))
            })
    }
}
