import {Dispatch} from 'redux';
import {loginApi, LoginType, ResponseLoginType} from '../Login/loginApi';

const initialState = {
    isLoggedIn: false as boolean,
    user: {
        id: '',
        email: '',
        name: '',
        publicCardPacksCount: 0, // количество колод
        created: Date,
        updated: Date,
        isAdmin: false,
        verified: false, // подтвердил ли почту
        rememberMe: false
    } as ResponseLoginType | {},
};

export const loginReducer = (
    state: InitialStateType = initialState,
    action: ActionsType
): InitialStateType => {
    switch (action.type) {
        case 'LOGIN/SET-IS-LOGGED-IN':
            return {...state, isLoggedIn: action.value};
        case 'LOGIN/SET_USER_DATA':
            // debugger
            return {...state, user: action.userData, id: action.userData._id};
        default:
            return state;
    }
};

// actions
export const setIsLoggedInAC = (value: boolean) =>
    ({type: 'LOGIN/SET-IS-LOGGED-IN', value} as const);

export const setUserDataAC = (userData: ResponseLoginType) => {
    //  debugger
    return (
        {
            type: 'LOGIN/SET_USER_DATA',
            userData,
        } as const);
}

// thunks
export const authThunkCreator = () => {
    return (dispatch: any) => {
        return loginApi.me().then(data => {
            if(data.resultCode === 0){
                let {id, login, email} = data.data
                dispatch(SetAuthUserDataAC(id, email, login, true))
            }
        })
    }
}

export const loginTC =
    (data: LoginType) => (dispatch: Dispatch<ActionsType>) => {
        loginApi
            .login(data)
            .then((res) => {
                dispatch(setIsLoggedInAC(true));
                dispatch(setUserDataAC(res.data))
            })
            .catch();
    };
export const logoutTC = () => (dispatch: Dispatch<ActionsType>) => {
    loginApi
        .logout()
        .then((res) => {
            dispatch(setIsLoggedInAC(false));
        })
        .catch();
};

