// types
type InitialStateType = typeof initialState;

export type setIsLoggedInACType = ReturnType<typeof setIsLoggedInAC>;
type ActionsType =
    | ReturnType<typeof setIsLoggedInAC>
    | ReturnType<typeof setUserDataAC>
